import React, { useState } from "react";
import NewsDataService from "./../services/NewsService/NewsService";
import { CKEditor } from "@ckeditor/ckeditor5-react";
import ClassicEditor from "@ckeditor/ckeditor5-build-classic";
import { Box, Button, FormField, TextInput, Heading, Form } from "grommet";
import { useDispatch } from "react-redux";

function AddNews() {
  const initialNewsState = {
    id: null,
    NewsTitle: "",
    News: "",
    City: "",
    Image: "",
  };
  const dispatch=useDispatch();
  let user = sessionStorage.getItem("user");
  let u = JSON.parse(user);
  console.log(u.id);
  const [file, setFile] = useState("");
  const [filename, setFilename] = useState("");
  const [val, setVal] = useState("");
  const [news, setNews] = useState(initialNewsState);
  const [submitted, setSubmitted] = useState(false);

  const handleInputChange = (event) => {
    const { name, value } = event.target;
    setNews({ ...news, [name]: value });
  };

  const handleOnChange = (event, editor) => {
    const data = editor.getData();
    setVal(data);
  };

  const onChange = async(event) => {
   await setFile(event.target.files[0]);
    await setFilename(event.target.files[0].name);
  };

  const saveNews = async (event) => {
    event.preventDefault();
    let formdata = new FormData();

    formdata.append("NewsTitle", news.NewsTitle);
    formdata.append("News", val);
    formdata.append("City", news.City);
    formdata.append("Image", filename);
    formdata.append("file", file);
    formdata.append("Rid", u.id);
    console.log(formdata);

    await NewsDataService.create(formdata)
      .then((response) => {
        setNews({
          id: response.data.id,
          NewsTitle: response.data.NewsTitle,
          News: response.data.News,
          City: response.data.City,
          Image: response.data.Image,
        });

        setSubmitted(true);
        d
        console.log(response.data);
      })
      .catch((e) => {
        console.log("Error");
        console.log(e);
      });
  };

  // const addMoreNews = (event) => {
  //   event.preventDefault();
  //   setNews(initialNewsState);
  //   setSubmitted(false);
  // };

  return (
    

    <Box align='center'>
      {submitted ? (
        <Box align='center'>
          <Heading>Hi !!! Posted SuccessFully</Heading>
          {/* <Button primary onClick={addMoreNews}label='Post News'></Button> */}
        </Box>
      ) : (
        <Box align='center' background='light-2' width='large'>
          <Form onSubmit={saveNews}>
          <Heading>Post Today's Top News</Heading>

          <FormField
            name="NewsTitle"
            htmlfor="text-input-id"
            label="NewsTitle"
            required
          >
            <TextInput
              type="text"
              name="NewsTitle"
              value={news.NewsTitle}
              onChange={handleInputChange}
            />
          </FormField>

          <Box>
            {" "}
            <CKEditor
              name="News"
              className="form-control"
              y
              editor={ClassicEditor}
              value={news.News}
              onChange={handleOnChange}
            />
          </Box>

          <FormField name="City" htmlfor="text-input-id" label="City" required>
            <TextInput
              type="text"
              name="City"
              value={news.City}
              onChange={handleInputChange}
            />
          </FormField>
          <FormField
            name="Image"
            htmlfor="text-input-id"
            label="Image"
            required
          >
            <TextInput type="file" name="Image" onChange={onChange} />
          </FormField>

          <Button primary  label='Post' type='submit'></Button>
          </Form>
        </Box>
      )}
    </Box>
  );
}

export default AddNews;
