import React from "react";

import { useState, useEffect } from "react";

import { Link } from "react-router-dom";

import { getAll } from "../../services/NewsServicep/NewsServicep";

import {
  Box,
  Grommet,
  Header,
  Table,
  TableBody,
  TableCell,
  TableHeader,
  TableRow,
} from "grommet";
import { grommet } from "grommet/themes";

export const NewsList = () => {
  const [news, setNews] = useState();

  useEffect(() => {
    async function retrieveNews() {
      await getAll().then((result) => setNews(result));
    }
    retrieveNews();
  }, []);

  return (
    // const CollapsableNav = () => (
    <Grommet theme={grommet}>
      <Header background="dark-1" pad="medium" align="center">
        <Box direction="row" align="center" gap="small">
          Admin Dashboard
        </Box>
      </Header>

      <Box
        width="100vw"
        height="100vh"
        alignContent="center"
        pad="large"
        overflow="auto"
      >
        <Table>
          <TableHeader>
            <TableRow>
              <TableCell scope="col" border="bottom">
                <strong> News Title</strong>
              </TableCell>
              <TableCell scope="col" border="bottom">
                <strong> News Link</strong>
              </TableCell>
            </TableRow>
          </TableHeader>
          <TableBody>
            {news &&
              news.map((newslist, i) => (
                <TableRow>
                  <strong>
                    <TableCell scope="row">{newslist.NewsTitle}</TableCell>
                  </strong>
                  <TableCell>
                    <Link to={`/FullNews/${newslist.id}`}>
                      {newslist.NewsTitle}
                    </Link>
                  </TableCell>
                </TableRow>
              ))}
          </TableBody>
        </Table>
      </Box>
    </Grommet>
  );
};

export default NewsList;
