import React, { useEffect, useState } from 'react'
import { Box, Heading,Image, Button, Select } from 'grommet'
import { useParams } from 'react-router-dom';
import NewsDataService from "./../services/NewsService/NewsService";
import ReactHtmlParser from 'react-html-parser';
function News() {
    const [news,setNews]=useState()
    const [value, setValue] = useState('');
    
    const [issue,setIssue]=useState(false);
    const {id}=useParams();
    useEffect(()=>{
        retrieveNews();
    },[])

    const retrieveNews=()=>{
        NewsDataService.FindOne(id).then(response=>{
            setNews(response.data);
            console.log(response.data);
           
        }).
        catch(err=>{
            if(err)
            {
                console.log(err);
            }
        })

    }
    console.log(news)
    return (
        <Box  height='full' align='center' width='full'>
          <Box background='light-2' width='large' align='center' height='full'>
              <Heading size='small'> {news && news.NewsTitle}</Heading>
              <Image src={`../images/${news && news.Image}`} fill='horizontal' />
              
               <Box>{news && ReactHtmlParser(news.News)}</Box>
               <Button primary label='Report' onClick={setIssue()}></Button>
               {issue? 
               <Box>
                   <Heading>Report Issue</Heading>
                   <Select
      options={['Child Abuse', 'Promotes terrorism', 'Hateful content']}
      value={value}
      size='small'
      onChange={({ option }) => setValue(option)}
    />
                </Box>:<p></p>
               }
          </Box>
        </Box>
    )
}

export default News
