        // <button className="btn btn-success" onClick={saveNews}>Post</button>
     
        // </div>
        // )}
        // </Box>

        <Box>
           {submitted ? (
          <Box>
          <Heading>Hi !!! Posted SuccessFully</Heading>
          <Button onClick={addMoreNews}>
          Post News
          </Button>
          </Box>
          ): (

              <Box>
              <Heading>Post Today's Top News</Heading>
             
              <FormField name="name" htmlfor="text-input-id" label="Name" required>
              <TextInput type='text' name="NewsTitle" value={news.NewsTitle}  onChange={handleInputChange}/>
             </FormField>
                
                <Box> <CKEditor 
            name="News"
            className="form-control"y
            
            editor={ClassicEditor}
            value={news.News}
            onChange={handleOnChange} 
            /></Box>
    
            
    
            <div className="form-group t-2">
                <input
                type="text"
                className="form-control"
                id="City"
                required
                placeholder="City"
                value={news.City}
               onChange={handleInputChange}
                name="City"
              />
            </div>
    
            <div className="form-group t-2">
                <input
                type="file"
                className="form-control"
                id="Image"
                required
               onChange={onChange}
                name="Image"
              />
            </div>
    
            <button className="btn btn-success" onClick={saveNews}>Post</button>
         
