import React from 'react';
import { Menu, Article, Add, Home, Login,UserAdd} from 'grommet-icons';
import {
  Anchor,
  Box,
  Grommet,
  Header,
  Nav,
 
  ResponsiveContext,
  DropButton,
} from 'grommet';
import { grommet } from 'grommet/themes';
import AdminHeader from './AdminHeader';
import ReporterHeader from './ReporterHeader';
import { Link, NavLink } from 'react-router-dom';
const user=JSON.parse(localStorage.getItem('user'));
let role=user.role;



export const Collapsable = (props) => {
  console.log(props.islogged)
  console.log(role)
  if(props.islogged===false)
  {
  return(
  <Grommet theme={grommet}>
    <Header background="dark-1" pad="medium">
      <Box direction="row" align="center" gap="small">
        <Article />
      </Box>
      <ResponsiveContext.Consumer>
        {(responsive) =>
          responsive === "small" ? (
           
            <DropButton
            icon={<Menu />}
              dropAlign={{ top: "bottom", right: "right" }}
              dropContent={<Box pad="large" width="100vw" background="light" justify='center'>
              
              <NavLink exact to='/' activeStyle={{fontWeight: "bold",color: "red"}}><Home></Home>Home</NavLink>
                <NavLink exact to='/Login'  activeStyle={{fontWeight: "bold",color: "red"}}><Login></Login>Login</NavLink>
                <NavLink exact to='/Signup'  activeStyle={{fontWeight: "bold",color: "red"}}><UserAdd></UserAdd>Signup</NavLink>
              
              </Box>}
            />
          ) : (
            <Nav direction="row" >
              
               <NavLink exact to='/' title='Home' ><Home ></Home></NavLink>
                <NavLink exact to='/Login'><Login></Login></NavLink>
                <NavLink exact to='/Signup'><UserAdd></UserAdd></NavLink>

              
              
            </Nav>
          )
        }
      </ResponsiveContext.Consumer>
    </Header>
  </Grommet>
  )}
  else if(props.islogged===true && role=='admin'){
    return(
     <AdminHeader></AdminHeader>
    )
  }
  else if(props.islogged===true && role=='reporter'){
    return(
     <ReporterHeader></ReporterHeader>
    )

  }




};
