import React, { useState } from 'react';
import { FormField, TextInput, Box, Button, Form, Heading, MaskedInput } from 'grommet';

const emailMask = [
    {
      regexp: /^[\w\-_.]+$/,
      placeholder: 'example',
    },
    { fixed: '@' },
    {
      regexp: /^[\w]+$/,
      placeholder: 'my',
    },
    { fixed: '.' },
    {
      regexp: /^[\w]+$/,
      placeholder: 'com',
    },
  ];




// This example shows a way to perform validation across multiple fields.
export const SignUp = () => {
    const [value,setValue]=useState({Username:'a',email:'',Username:''
    ,password:'',cpassword:'',city:'',mobile:'',aadhar:''})
    const HandleData=(event)=>{
        console.log("reached");
    }
 return(
     <Box align="center" justify="between" height="90vh" >
         <Box margin={"top","-1vh"} align="center">
         <Heading>SignUp</Heading>
         <Form
            value={value}
            onChange={nextValue => setValue(nextValue)}
            onSubmit={({ value: nextValue }) => console.log(nextValue)}

          >
              <Box direction="row-responsive">
                  <Box margin={"right","2vw"}>
             <FormField label="Username" name="Username" required>
              <TextInput name="Username" type="text" />
            </FormField>
            <FormField label="Email" name="email" required>
            <MaskedInput
              name="email"
              mask={emailMask}
            />
            </FormField>
            <FormField label="Password" name="password" required>
              <TextInput name="password" type="password" />
            </FormField>
            <FormField label=" Confirm Password" name="cpassword" required>
              <TextInput name="cpassword" type="password" />
            </FormField>
            </Box>
            <Box margin={"left","2vw"}>
            <FormField label="City" name="city" required>
              <TextInput name="city" type="text" />
            </FormField>
            <FormField name="mobile" label="Mobile no" required>
            <MaskedInput
              name="mobile"
              mask={[
                { fixed: '(' },
                {
                  length: 3,
                  regexp: /^[0-9]{1,3}$/,
                  placeholder: 'xxx',
                },
                { fixed: ')' },
                { fixed: ' ' },
                {
                  length: 3,
                  regexp: /^[0-9]{1,3}$/,
                  placeholder: 'xxx',
                },
                { fixed: '-' },
                {
                  length: 4,
                  regexp: /^[0-9]{1,4}$/,
                  placeholder: 'xxxx',
                },
              ]}
            />
          </FormField>
           
            <FormField label="Aadhar No" name="aadhar" required>
              <TextInput name="addhar" type="tel" />
            </FormField>

            {/* {message && (
              <Box pad={{ horizontal: 'small' }}>
                <Text color="status-error">{message}</Text>
              </Box>
            )} */}

            </Box>
            </Box>
            <Box direction="row" justify="center" margin={{ top: 'medium' }}>
            <Button type="submit" label="Signup" primary />
              <Button type="reset" label="Login" />
              
            </Box>
          </Form>
    </Box>
    </Box>
 );
  
};
