import React from "react";
import { useState, useEffect } from "react";

import {
  getAll,
  update,
  remove,
} from "../../services/UserRequestsService/UserRequestsService";
import {
  Box,
  Grommet,
  Header,
  Button,
  Table,
  TableBody,
  TableCell,
  TableHeader,
  TableRow,
  Card,
  CardBody,
} from "grommet";
import { grommet } from "grommet/themes";
import { confirmAlert } from "react-confirm-alert";

export const UserRequests = () => {
  const [users, setUsers] = useState();

  useEffect(() => {
    async function users() {
      await getAll().then((result) => setUsers(result));
    }
    users();
  }, []);

  function confirm(id) {
    let getId = id;
    confirmAlert({
      title: "Confirm to Delete News",
      message: "Are you sure to delete this news.",
      buttons: [
        {
          label: "Yes",
          onClick: () => {
            // alert('Click Yes')
            userReject(getId);
          },
        },
        {
          label: "No",
          onClick: () => {
            // alert('Click No')
            userNoDelete();
          },
        },
      ],
    });
  }

  const isApprovedTrue = async (uid) => {
    alert(uid);
    let data = { email: users.EmailId, uid: uid };
    await update(data);
    await getAll().then((result) => setUsers(result));
  };

  const userReject = async (uid) => {
    alert(uid);
    let dataRejected = { id: uid };
    await remove(dataRejected);
    await getAll().then((result) => setUsers(result));
  };

  const userNoDelete = async () => {
    await getAll().then((result) => setUsers(result));
  };

  // function confirm() {
  //   confirmAlert({
  //     title: "Confirm to Delete News",
  //     message: "Are you sure to delete this news.",
  //     buttons: [
  //       {
  //         label: "Yes",
  //         onClick: () => {
  //           // alert('Click Yes')
  //           fullNewsDelete();
  //         },
  //       },
  //       {
  //         label: "No",
  //         onClick: () => {
  //           // alert('Click No')
  //           fullNewsNoDelete();
  //         },
  //       },
  //     ],
  //   });
  // }
  // const fullNewsDelete = async () => {
  //   let dataRejected = id;
  //   alert(dataRejected);
  //   await remove(dataRejected);

  //   history.push("/");
  // };

  // const fullNewsNoDelete = async () => {
  //   history.push(`/FullNews/${id}`);
  // };

  return (
    <Grommet theme={grommet}>
      <Box
        width="100vw"
        height="100vh"
        alignContent="center"
        pad="large"
        overflow="auto"
      >
        <Card
          alignSelf="center"
          height="large"
          width="100%"
          background="light-1"
        >
          <CardBody pad="large">
            
          </CardBody>
        </Card>
      </Box>
    </Grommet>
  );
};

export default UserRequests;
