import React, { useState, useEffect } from 'react'
import { Box, Grid, Heading,Card,Text,Image } from 'grommet'
import { NavLink } from 'react-router-dom';
import NewsDataService from '../../services/NewsService/NewsService'
export default function ReporterHome() {
  const [news,setNews]=useState()

  const user=sessionStorage.getItem('user')
  const parsed=JSON.parse(user);
  const id=parsed.id;

  useEffect(()=>{
    reporterNews()
  },[])
  const reporterNews = () => {
    NewsDataService.reporterNews(id)
      .then((response) => {
        setNews(response.data)
      })
      .catch((err) => {
        if (err) {
          console.log(err)
        }
      })
  }
  return (
    <Box basis='full' pad='medium' width='full' align='center'>
      <Heading size='small' textAlign='center'>
        Reporter's News
      </Heading>
      <Grid gap='20px'>
        {news &&
          news.map((n, index) => (
            <Card key={index} direction='row' height='10vh'>
              <Image src={`../images/${n && n.Image}`} width='30%'></Image>
              <NavLink
                to={`/News/${n.id}`}
                
                style={{
                  textDecoration: 'none',
                  marginTop: '3vh',
                  marginLeft: '1vw',
                }}
              >
                <Text color='black' weight='bold'>
                  {n.NewsTitle}
                </Text>
              </NavLink>
            </Card>
          ))}
      </Grid>
    </Box>
  )
}
