import axios from 'axios'


var msg='';
export function RegisterUser(data)
{ 
    const [msg,setMsg]=useS

    axios.post('http://localhost:1337/addUser',data)
    .then((response)=>{
        
        if(response.data.code=='E_UNIQUE')
        {
           
            msg="email already exist";
            console.log(msg);
        }
        else{
            msg="Sucessfully Registered";
           
        }
    },
    (error)=>{
        console.log(error);
    }

    )
    
    return msg;
}


export function FindUser(data)
{
    axios.post('http://localhost:1337/findUser',data)
    .then((response)=>{
        console.log(response.data.user)
    },
    (error)=>{
        console.log(error);
    }

    )
}
