import React from "react";
import { useState, useEffect } from "react";

import {
  getAll,
  update,
  remove,
} from "../../services/UserRequestsService/UserRequestsService";
import {
  Box,
  Grommet,
  Header,
  Button,
  Table,
  TableBody,
  TableCell,
  TableHeader,
  TableRow,
} from "grommet";
import { grommet } from "grommet/themes";

export const UserRequests = () => {
  const [users, setUsers] = useState();

  useEffect(() => {
    async function users() {
      await getAll().then((result) => setUsers(result));
    }
    users();
  }, []);

  const isApprovedTrue = async (uid) => {
    alert(uid);
    let data = { email: users.EmailId, uid: uid };
    await update(data);
    await getAll().then((result) => setUsers(result));
  };

  const userReject = async (uid) => {
    alert(uid);
    let dataRejected = { id: uid };
    await remove(dataRejected);
    await getAll().then((result) => setUsers(result));
  };

  return (
    <Grommet theme={grommet}>
      <Box
        width="100vw"
        height="100vh"
        alignContent="center"
        pad="large"
        overflow="auto"
      >
        <Table>
          <TableHeader>
            <TableRow>
              <TableCell scope="col" border="bottom">
                <strong> Name</strong>
              </TableCell>
              <TableCell scope="col" border="bottom">
                <strong> Locality</strong>
              </TableCell>
              <TableCell scope="col" border="bottom">
                <strong> AadharId</strong>
              </TableCell>

              <TableCell scope="col" border="bottom">
                <strong> Approve</strong>
              </TableCell>
              <TableCell scope="col" border="bottom">
                <strong> Reject</strong>
              </TableCell>
            </TableRow>
          </TableHeader>
          <TableBody>
            {users &&
              users.map((userslist, i) => (
                <TableRow>
                  <TableCell scope="row">{userslist.Uname}</TableCell>
                  <TableCell scope="row">{userslist.Locality}</TableCell>
                  <TableCell scope="row">{userslist.AadharId}</TableCell>

                  <TableCell scope="row">
                    <Button
                      primary
                      label="Approve"
                      alignSelf="start"
                      onClick={() => isApprovedTrue(userslist.id)}
                    />
                  </TableCell>
                  <TableCell scope="row">
                    <Button
                      primary
                      label="Reject"
                      alignSelf="start"
                      onClick={() => userReject(userslist.id)}
                    />
                  </TableCell>
                </TableRow>
              ))}
          </TableBody>
        </Table>
      </Box>
    </Grommet>
  );
};

export default UserRequests;
