import React, { useEffect, useState } from "react";
import {
  Box,
  Heading,
  Image,
  Button,
  Select,
  TextInput,
  Text,
  InfiniteScroll,
} from "grommet";
import { useParams } from "react-router-dom";
import NewsDataService from "./../services/NewsService/NewsService";
import IssueDataService from "./../services/IssueService/IssueService";
import ReactHtmlParser from "react-html-parser";
import { Facebook } from "grommet-icons";
function News() {
  const [news, setNews] = useState();
  const [message, setMessage] = useState("");
  const [value, setValue] = useState("");
  const [email, setEmail] = useState("");
  const [issue, setIssue] = useState(false);
  const { id } = useParams();
  useEffect(() => {
    retrieveNews();
  }, []);

  const retrieveNews = () => {
    NewsDataService.FindOne(id)
      .then((response) => {
        setNews(response.data);
        console.log(response.data);
      })
      .catch((err) => {
        if (err) {
          console.log(err);
        }
      });
  };

  const saveIssue = () => {
    const data = {
      email: email,
      issue: value,
      nId: news.id,
    };
    console.log(data);
    IssueDataService.create(data)
      .then((response) => {
        setMessage("Issue Reported Successfully");
        
        console.log(response.data);
      })
      .catch((err) => {
        if (err) {
          console.log(err);
        }
      });
  };

  console.log(news);
  return (
    <Box width="full" align='center'>
      <Box background="light-2" width="large" align="center">
        <Heading size="small"> {news && news.NewsTitle}</Heading>
        <Image src={`../images/${news && news.Image}`} fill="horizontal" />
       
        <Box>{news && ReactHtmlParser(news.News)}</Box>
       {issue ? <Button primary label='Cancel' onClick={() => setIssue(false)}></Button>:<Button primary label="Report" onClick={() => setIssue(true)}></Button>}
  
        {issue ? (
          <Box size="small">
            <Heading>Report Issue</Heading>
            <Select
              placeholder="Choose Issue"
              options={["Child Abuse", "Promotes terrorism", "Hateful content"]}
              value={value}
              size="small"
              onChange={({ option }) => setValue(option)}
            />
            <br></br>
            <TextInput
              type="text"
              name="email"
              required
              onChange={(event) => setEmail(event.target.value)}
              placeholder="Email"
            />
            <Button primary label="Report" onClick={saveIssue} margin={{'bottom':'5px'}}></Button>
            {message && (
              <Box pad={{ horizontal: "medium" }}>
                <Text color="#00C781">{message}</Text>
              </Box>
            )}
          </Box>
        ) : (
          <p></p>
        )}
      </Box>
    </Box>
  );
}

export default News;
