import React, { useState, useEffect } from 'react'
import { Box, Text, Grid, Heading } from 'grommet'
import { getReporter } from '../../call/UserCall'
import NewsDataService from '../../services/NewsService/NewsService'

export default function ReporterProfile() {
  const [user, setUser] = useState()
  const u = sessionStorage.getItem('user')
  const parseduser = JSON.parse(u)
  const rId = parseduser.id
  console.log(rId)
  useEffect(() => {
    retrieveReporter()
  }, [])
  const retrieveReporter = () => {
    NewsDataService.getReporter(rId)
      .then((response) => {
        setUser(response.data)
      })
      .catch((err) => {
        if (err) {
          console.log(err)
        }
      })
  }
  console.log(user)
  return (
    <Box align='center'>
     <Box width='large' pad='small' align='center'>
     <Grid
      columns={{
        count: 2,
        size: 'auto',
      }}
      rows={['auto', 'flex']}
      gap='small'
     border='all'
    >
        <Box><Heading size='small' level='4'>User Id</Heading></Box>
    <Heading size='small' level='4'>{user && user.Uid}</Heading>
        <Heading size='small' level='4'>User Id</Heading>
        <Heading size='small' level='4'>User Id</Heading>
    </Grid>
     </Box>
    </Box>
  )
}
