import React, { useState } from 'react';
import { FormField, TextInput, Box, Button, Form, Heading, MaskedInput } from 'grommet';
import { useHistory } from 'react-router-dom';
import { User, MailOption, Lock, Directions, Phone, Hide } from 'grommet-icons';

const emailMask = [
    {
      regexp: /^[\w\-_.]+$/,
      placeholder: 'example',
    },
    { fixed: '@' },
    {
      regexp: /^[\w]+$/,
      placeholder: 'my',
    },
    { fixed: '.' },
    {
      regexp: /^[\w]+$/,
      placeholder: 'com',
    },
  ];




// This example shows a way to perform validation across multiple fields.
export const SignUp = () => {
    let history=useHistory();
    const [value,setValue]=useState({Username:'',email:'',Username:''
    ,password:'',cpassword:'',city:'',mobile:'',aadhar:''})
    const HandleData=(event)=>{
        console.log("reached");
    }

   const ShowPass=(event)=>{
        if(event.target.type=='password')
        event.target.type="text";
        else
        event.target.type="password";
    }
 return(
     <Box align="center" justify="between" height="90vh" >
         <Box margin={"top","-1vh"} align="center">
         <Heading>SignUp</Heading>
         <Form
            value={value}
            onChange={nextValue => setValue(nextValue)}
            onSubmit={({ value: nextValue }) => console.log(nextValue)}

          >
              <Box direction="row-responsive">
                  <Box margin={"right","2vw"}>
             <FormField label="Username" name="Username" required>
              <TextInput icon={<User />} name="Username" type="text" />
            </FormField>
            <FormField label="Email" name="email" required>
            <TextInput name="email" icon={<MailOption />} type="email" pattern="[a-z0-9._%+-]{1,40}[@]{1}[a-z]{1,10}[.]{1}[com]{3}" placeholder="abc@gmail.com" />
            </FormField>
            <FormField label="Password" name="password" required>
              <TextInput name="password" icon={ <Lock />}  type="password" />
            </FormField>
            <FormField label=" Confirm Password" name="cpassword" required>
              <TextInput reverse name="cpassword" icon={< Lock onClick={ShowPass(event)}/>} type="password" />
            </FormField>
            </Box>
            <Box margin={"left","2vw"}>
            <FormField label="City" name="city" required>
              <TextInput icon={<Directions />} name="city" type="text" />
            </FormField>
            <FormField name="mobile" label="Mobile no" required>
            <MaskedInput
              name="mobile"
              icon={<Phone/>}
              pattern="[0-9]{1,3}[-]{1}[0-9]{1,3}[-]{1}[0-9]{1,4}"
              mask={[
               
                {
                  length: 3,
                  regexp: /^[0-9]{1,3}$/,
                  placeholder: 'xxx',
                },
               
                { fixed: '-' },
                {
                  length: 3,
                  regexp: /^[0-9]{1,3}$/,
                  placeholder: 'xxx',
                },
                { fixed: '-' },
                {
                  length: 4,
                  regexp: /^[0-9]{1,4}$/,
                  placeholder: 'xxxx',
                },
              ]}
            />
          </FormField>
           
            <FormField label="Aadhar No" name="aadhar" required>
            <MaskedInput
              name="aadhar"
              pattern="[0-9]{1,4}[-]{1}[0-9]{1,4}[-]{1}[0-9]{1,4}"
              mask={[
               
                {
                  length: 4,
                  regexp: /^[0-9]{1,4}$/,
                  placeholder: 'xxxx',
                
                },
                { fixed: '-' },
                
                {
                  length: 3,
                  regexp: /^[0-9]{1,4}$/,
                  placeholder: 'xxxx',
                },
                { fixed: '-' },
                {
                  length: 4,
                  regexp: /^[0-9]{1,4}$/,
                  placeholder: 'xxxx',
                },
              ]}>
              </MaskedInput>
            </FormField>

            {/* {message && (
              <Box pad={{ horizontal: 'small' }}>
                <Text color="status-error">{message}</Text>
              </Box>
            )} */}

            </Box>
            </Box>
            <Box direction="row" justify="center" margin={{ top: 'medium' }}>
            <Button type="submit" label="Signup" primary margin={"right","1vw"} />
              <Button  label="Login" margin={"left","1vw"} onClick={()=>{history.push('/Login')}}/>
              
            </Box>
          </Form>
    </Box>
    </Box>
 );
  
};
