import React from "react";
import { useState, useEffect, useContext } from "react";
import { useHistory } from "react-router-dom";

import {
  Button,
  Box,
  Grid,
  Heading,
  CardBody,
  Card,
  ResponsiveContext,
  Table,
  TableBody,
  TableCell,
  TableHeader,
  TableRow,
} from "grommet";
import { grommet } from "grommet/themes";
import { Grommet } from "grommet";
import {
  getAll,
  remove,
} from "../../services/FullNewsServices/FullNewsServices";

import { confirmAlert } from "react-confirm-alert";
import "react-confirm-alert/src/react-confirm-alert.css";

export const FullNews = (props) => {
  let history = useHistory();

  const [news, setNews] = useState();
  const size = useContext(ResponsiveContext);
  let id = props.match.params.id;
  //alert(id);
  useEffect(() => {
    async function news() {
      await getAll(id).then((result) => setNews(result));
    }
    news();
  }, []);

  function confirm() {
    confirmAlert({
      title: "Confirm to Delete News",
      message: "Are you sure to delete this news.",
      buttons: [
        {
          label: "Yes",
          onClick: () => {
            // alert('Click Yes')
            fullNewsDelete();
          },
        },
        {
          label: "No",
          onClick: () => {
            // alert('Click No')
            fullNewsNoDelete();
          },
        },
      ],
    });
  }
  const fullNewsDelete = async () => {
    let dataRejected = id;
    alert(dataRejected);
    await remove(dataRejected);

    history.push("/");
  };

  const fullNewsNoDelete = async () => {
    history.push(`/FullNews/${id}`);
  };

  return (
    <Grommet theme={grommet}>
      {/* { <Box
        width="100vw"
        height="100vh"
        alignContent="center"
        pad="large"
        overflow="auto"
      > 
             
        <Grid gap="20px">
          <Card
            align="left"
            height="medium"
            width="medium"
            background="light-1"
          >
            <CardBody pad="large">
              <h2>News</h2>
              <p>
                {news && news.NewsTitle} <br></br>
              </p>
              <p>
                {" "}
                {news && news.News} <br></br>
              </p>{" "}
               <h2>Reported Issues</h2>
              {news &&
                news.issues.map((newslist, index) => (
                  <Table>
                    <TableHeader></TableHeader>
                    <TableBody>
                      <TableRow>
                        <TableCell scope="row">
                          <li key={index}>{newslist.Issue}</li>
                        </TableCell>
                      </TableRow>
                    </TableBody>
                  </Table>
                ))}
              <hr></hr>
              <Button
                primary
                label="Delete News"
                alignSelf="start"
                color="#ff726f"
                onClick={() => confirm()}
              /> 
            </CardBody>
          </Card>
        </Grid>

        <Grid gap="20px">
          <Card
            align="left"
            height="medium"
            width="medium"
            background="light-1"
          >
            <CardBody pad="large">
               <h2>News</h2>
              <p>
                {news && news.NewsTitle} <br></br>
              </p>
              <p>
                {" "}
                {news && news.News} <br></br>
              </p>{" "}
              <hr></hr> 
              <h2>Reported Issues</h2>
              {news &&
                news.issues.map((newslist, index) => (
                  <Table>
                    <TableHeader></TableHeader>
                    <TableBody>
                      <TableRow>
                        <TableCell scope="row">
                          <li key={index}>{newslist.Issue}</li>
                        </TableCell>
                      </TableRow>
                    </TableBody>
                  </Table>
                ))}
              <hr></hr>
              <Button
                primary
                label="Delete News"
                alignSelf="start"
                color="#ff726f"
                onClick={() => confirm()}
              />
            </CardBody>
          </Card>
        </Grid>
      </Box>  */}

      <Box direction="row-responsive">
        <Box width="medium" pad="large">
        
          <Grid gap="20px">
            <Card
              align="left"
              height="large"
              width="large"
              background="light-1"
              direction="row"
            >
              <CardBody pad="large">
                <h2>News</h2>
                <p>
                  {news && news.NewsTitle} <br></br>
                </p>
                <p>
                  {" "}
                  {news && news.News} <br></br>
                </p>{" "}
                {/* <h2>Reported Issues</h2>
              {news &&
                news.issues.map((newslist, index) => (
                  <Table>
                    <TableHeader></TableHeader>
                    <TableBody>
                      <TableRow>
                        <TableCell scope="row">
                          <li key={index}>{newslist.Issue}</li>
                        </TableCell>
                      </TableRow>
                    </TableBody>
                  </Table>
                ))}
              <hr></hr>
              <Button
                primary
                label="Delete News"
                alignSelf="start"
                color="#ff726f"
                onClick={() => confirm()}
              /> */}
              </CardBody>
            </Card>
          </Grid>
        </Box>
        <Box basis="full" pad="medium" width="full">
         
          <h2>Reported Issues</h2>
          <Card
              
              height="large"
              width="large"
              background="light-1"
              direction="row"
            >
              <CardBody pad="large">
          {news &&
            news.issues.map((newslist, index) => (
              <Table>
                <TableHeader></TableHeader>
                <TableBody>
                  <TableRow>
                    <TableCell scope="row">
                      <li key={index}>{newslist.Issue}</li>
                    </TableCell>
                  </TableRow>
                </TableBody>
              </Table>
            ))}
          <hr></hr>
          <Button
            primary
            label="Delete News"
            alignSelf="start"
            color="#ff726f"
            onClick={() => confirm()}
          />
        
        </CardBody>
            </Card>
      </Box>
      </Box>
    </Grommet>
  );
};

export default FullNews;
