import React from 'react'
import { Route, Switch, Redirect } from 'react-router-dom'
import { SignUp } from '../component/User/SignUp'
import { Box } from 'grommet'
import {IndexApp} from '../component/Admin/IndexApp'
import ContainLogin from '../container/containsign'
import Home from '../component/User/Home'
import ReporterHome from '../component/Reporter/ReporterHome'
import AddNews from '../component/Reporter/AddNews'
import News from '../component/User/News'
import NewsList from '../component/Admin/NewsList'
import ReporterViewNews from '../component/Reporter/ReporterViewNews'
import ReporterProfile from '../component/Reporter/ReporterProfile'
import { useSelector } from 'react-redux'
import { GuardProvider, GuardedRoute } from 'react-router-guards';
export function Navi(props) {
  
    let islogged=useSelector((state)=>state.islogged)
    const requireLogin = (to, from, next) => {
      if (to.meta.auth) {
        if (islogged) {
          next();
        }
        next.redirect('/Login');
      } else {
        next();
      }
    };
  return (
    <Box style={{ 'padding-bottom': '2.5rem' }}>
          <GuardProvider guards={[requireLogin]}  error={NotFound}>
      <Switch>
        <Route exact path='/' component={Home} />
        <Route path='/Login' component={ContainLogin}></Route>
        <Route path='/Signup' component={SignUp}></Route>
        <Route path='/Admin' component={IndexApp}></Route>
        <Route path='/Admin/IndexApp' component={IndexApp}></Route>
        <Route exact path='/Reporter' component={ReporterHome}></Route>
        <Route path='/Reporter/AddNews' component={AddNews}></Route>
        <Route path='/Reporter/Profile' component={ReporterProfile}></Route>
        <Route path='/news/:id' component={News}></Route>
        <Route path='/Reporter/news/:id' component={ReporterViewNews}></Route>
        
      </Switch>
      </GuardProvider>
    </Box>
  )
}
