import React from "react";

import { useState, useEffect, useContext } from "react";

import { Link, NavLink } from "react-router-dom";

import { getAll } from "../../services/NewsServicep/NewsServicep";

import {
  Box,
  Grommet,
 
  ResponsiveContext,

  Heading,
  Grid,
  Card,
} from "grommet";
import { grommet } from "grommet/themes";

export const NewsList = () => {
  const [news, setNews] = useState();
  const size = useContext(ResponsiveContext);

  useEffect(() => {
    async function retrieveNews() {
      await getAll().then((result) => setNews(result));
      //log(news);
    }
    retrieveNews();
  }, []);

  return (
    // const CollapsableNav = () => (
    <Grommet theme={grommet}>
      {/* <Box
        width="100vw"
        height="100vh"
        alignContent="center"
        pad="large"
        overflow="auto"
      >
        <Table>
          <TableHeader>
            <TableRow>
              <TableCell scope="col" border="bottom">
                <strong> Image </strong>
              </TableCell>
              <TableCell scope="col" border="bottom">
                <strong> News</strong>
              </TableCell>
            </TableRow>
          </TableHeader>
          <TableBody>
            {news &&
              news.map((newslist, i) => (
                <TableRow>
                  <TableCell>
                    <Image
                      src={`../../images/${newslist && newslist.Image}`}
                      width="20%"
                    />

                    
                  </TableCell>
                  <TableCell>
                    <Link to={`/FullNews/${newslist.id}`}>
                      {newslist.NewsTitle}
                    </Link>
                  </TableCell>
                </TableRow>
              ))}
          </TableBody>
        </Table>
      </Box> */}

      {/* <Grid columns={size !== "small" ? "large" : "100%"} gap="large"> */}

      <Box
        basis="full"
        pad="medium"
        width={size !== "small" ? "large" : "100%"}
        align="center"
        gap="20px"
      >
        <Heading size="small" textAlign="center">
          All News
        </Heading>

        {news &&
          news.map((newslist, index) => (
            <Card
              key={index}
              direction="row"
              height="10vh"
              width="large"
              alignSelf="center"
            >
              <Image
                src={`../images/${newslist && newslist.Image}`}
                width="30%"
              ></Image>
              &nbsp;&nbsp;&nbsp;&nbsp;
              <Link to={`/FullNews/${newslist.id}`}>{newslist.NewsTitle}</Link>
            </Card>
          ))}
      </Box>
    </Grommet>
  );
};

export default NewsList;
