import React from "react";
import { useState, useEffect } from "react";

import {
  getAll,
  update,
  remove,
} from "../../services/UserRequestsService/UserRequestsService";
import {
  Box,
  Grommet,
  Header,
  Button,
  Table,
  TableBody,
  TableCell,
  TableHeader,
  TableRow,
} from "grommet";
import { grommet } from "grommet/themes";
import { confirmAlert } from "react-confirm-alert";

export const UserRequests = () => {
  const [users, setUsers] = useState();

  useEffect(() => {
    async function users() {
      await getAll().then((result) => setUsers(result));
    }
    users();
  }, []);



  function confirm(id) {
    let getId = id;
    confirmAlert({
      title: "Confirm to Delete News",
      message: "Are you sure to delete this news.",
      buttons: [
        {
          label: "Yes",
          onClick: () => {
            // alert('Click Yes')
            userReject(id);
          },
        },
        {
          label: "No",
          onClick: () => {
            // alert('Click No')
            userNoDelete();
          },
        },
      ],
    });
  }


  const isApprovedTrue = async (uid) => {
    alert(uid);
    let data = { email: users.EmailId, uid: uid };
    await update(data);
    await getAll().then((result) => setUsers(result));
  };

  const userReject = async (uid) => {
    alert(uid);
    let dataRejected = { id: uid };
    await remove(dataRejected);
    await getAll().then((result) => setUsers(result));
  };

const userNoDelete = async () => {
  await getAll().then((result) => setUsers(result));
  };

  // function confirm() {
  //   confirmAlert({
  //     title: "Confirm to Delete News",
  //     message: "Are you sure to delete this news.",
  //     buttons: [
  //       {
  //         label: "Yes",
  //         onClick: () => {
  //           // alert('Click Yes')
  //           fullNewsDelete();
  //         },
  //       },
  //       {
  //         label: "No",
  //         onClick: () => {
  //           // alert('Click No')
  //           fullNewsNoDelete();
  //         },
  //       },
  //     ],
  //   });
  // }
  // const fullNewsDelete = async () => {
  //   let dataRejected = id;
  //   alert(dataRejected);
  //   await remove(dataRejected);

  //   history.push("/");
  // };

  // const fullNewsNoDelete = async () => {
  //   history.push(`/FullNews/${id}`);
  // };




  return (
    <Grommet theme={grommet}>
      <Box
        width="100vw"
        height="100vh"
        alignContent="center"
        pad="large"
        overflow="auto"
      >
        <Table>
          <TableHeader>
            <TableRow>
              <TableCell scope="col" border="bottom">
                <strong> Name</strong>
              </TableCell>
              <TableCell scope="col" border="bottom">
                <strong> Locality</strong>
              </TableCell>
              <TableCell scope="col" border="bottom">
                <strong> AadharId</strong>
              </TableCell>

              <TableCell scope="col" border="bottom">
                <strong> Approve</strong>
              </TableCell>
              <TableCell scope="col" border="bottom">
                <strong> Reject</strong>
              </TableCell>
            </TableRow>
          </TableHeader>
          <TableBody>
            {users &&
              users.map((userslist, i) => (
                <TableRow>
                  <TableCell scope="row">{userslist.Uname}</TableCell>
                  <TableCell scope="row">{userslist.Locality}</TableCell>
                  <TableCell scope="row">{userslist.AadharId}</TableCell>

                  <TableCell scope="row">
                    <Button
                      primary
                      label="Approve"
                      alignSelf="start"
                      onClick={() => isApprovedTrue(userslist.Uid)}
                    />
                  </TableCell>
                  <TableCell scope="row">
                    <Button
                      primary
                      label="Reject"
                      color="#ff726f"
                      alignSelf="start"
                      onClick={() => confirm(userslist.Uid)}
                    />
                  </TableCell>
                </TableRow>
              ))}
          </TableBody>
        </Table>
      </Box>
    </Grommet>
  );
};

export default UserRequests;
