import React, { useEffect, useState } from 'react'
import { Box, Heading } from 'grommet'
import { useParams } from 'react-router-dom';
import NewsDataService from "./../services/NewsService/NewsService";
function News() {
    const [news,setNews]=useState()
    const {id}=useParams();
    
    useEffect(async()=>{
        console.log('sfsd')
        retrieveNews();
    },[])

    const retrieveNews=()=>{
        NewsDataService.FindOne(id).then(response=>{
            setNews(response.data);
            console.log(news);
           
        }).
        catch(err=>{
            if(err)
            {
                console.log(err);
            }
        })

    }
    console.log(news)
    return (
        <Box  height='full' align='center' width='full'>
           <Box width='large' background='light-2'>
    <Heading>{news.News}</Heading>
           </Box>
        </Box>
    )
}

export default News
