import React, { useEffect, useState, useContext } from 'react'
import { Box, CardFooter, Card, CardBody, Grid,Text, Button, CardHeader, ResponsiveContext } from 'grommet'
import { GetUser } from '../call/UserCall';
import { Favorite, ShareOption } from 'grommet-icons';




function AdminAccept() {
  const [users,setUsers]=useState([]);
  const size = useContext(ResponsiveContext);
    useEffect(async ()=>{
        await GetUser().then(result=>setUsers(result)); 
       
      
       },[])
   console.log(users)
    return (
       <Box width='100vw' height='100vh' alignContent='center'>
         <Grid columns={size !== 'small' ? 'small' : '100%'} gap="small">
            {users.map((user,i)=>{
              <Card  height="small" width="small" background="light-1">
              <CardHeader pad="medium">Request Permission</CardHeader>
              <CardBody pad="medium">Body</CardBody>
              <CardFooter pad={{horizontal: "small"}} background="light-2">   
              <Button
              icon={<Favorite color="red" />}
              hoverIndicator
            />
            <Button icon={<ShareOption color="plain" />} hoverIndicator />
            </CardFooter>
        </Card>
            })}

            </Grid>
       </Box>
    )
}

export default AdminAccept
