import React, { useState ,useEffect, useContext} from 'react'
import { Box, Header, Anchor, Nav, ResponsiveContext, Menu, Select, TextInput, Grid ,Card, Heading} from 'grommet'
import { Search } from 'grommet-icons';
import NewsDataService from "./../services/NewsService/NewsService";
import { Link } from 'react-router-dom';

function Home() {
    const options = ['title', 'locality'];
    const [searchValue, setSearchValue] = useState("");
    const [value, setValue] = useState('');
    const [news,setNews]=useState();
    const size = useContext(ResponsiveContext);
    useEffect(()=>{
      retrieveNews()
  },[]);

  
  const onChangeSearchValue = e => {
    const searchValue = e.target.value;
    setSearchValue(searchValue);
  };

  console.log(value);
  console.log(searchValue);

  const retrieveNews=()=>{
    NewsDataService.getAll().then(response=>{
        setNews(response.data);
        console.log(response.data);
       
    }).
    catch(err=>{
        if(err)
        {
            console.log(err);
        }
    })
}

  const findByTitleAndLocality = () => {

    if(value==="title")
    {
      console.log("1");
      NewsDataService.findByTitle(searchValue)
          .then(response => {
            console.log("3");
            setNews(response.data);
            console.log(response.data);
          })
          .catch(e => {
            console.log(e);
          });
    }
    else{
      console.log("2");
      NewsDataService.findByLocality(searchValue)
          .then(response => {
            console.log("4");
            setNews(response.data);
            console.log(response.data);
          })
          .catch(e => {
            console.log(e);
          });
    }
   
  };
    return (
        <Box >
             <Header background="dark-1" pad="medium">
    
      <ResponsiveContext.Consumer>
        {responsive =>
          responsive === 'small' ? (
            <Box fill align="start" justify="start" width="3vw" >
            <Select
              id="select"
              name="select"
              placeholder="Select"
              value={value}
              options={options}
              
              onChange={({ option }) => setValue(option)}
              open
              size='small'
            />
            <TextInput onKeyUp={findByTitleAndLocality} onChange={onChangeSearchValue} value={searchValue} icon={<Search />} placeholder="search ..." />
          </Box>
          ) : (
          <Box fill align="start" justify="start" direction='row-responsive'>
        <Select
          id="select"
          name="select"
          placeholder="Select"
          value={value}
          options={options}
          onChange={({ option }) => setValue(option)}
          open
          size='small'
        />
        <TextInput onKeyUp={findByTitleAndLocality}  onChange={onChangeSearchValue} value={searchValue}  icon={<Search />} placeholder="search ..." />
      </Box>
          )
        }
      </ResponsiveContext.Consumer>
    </Header>
  <Box direction='row-responsive'  >
    <Box basis='medium' border='all'>
    <Heading size='small' textAlign='center' >Top 10 News</Heading>
    </Box>
    <Box basis='full' border='all' pad='medium'  overflow='auto'>
      <Heading size='medium' textAlign='center'>All News</Heading>
    <Grid columns={size !== 'small' ? 'small' : '100%'} gap="small">
    {news &&
    news.map((n, index) => (
            
            <Card pad="large" key={index}>
              <Link t>{n.NewsTitle}</Link>
            </Card>
          ))}
        </Grid>
    </Box>
  </Box>
        </Box>
    )
}

export default Home




















