import React, { useState } from 'react'
import { Box, Header, Anchor, Nav, ResponsiveContext, Menu, Select } from 'grommet'

function Home() {
    const options = ['title', 'two'];
    const [value, setValue] = useState('');
    return (
        <Box>
             <Header background="dark-1" pad="medium">
    
      <ResponsiveContext.Consumer>
        {responsive =>
          responsive === 'small' ? (
            <Menu
              label="Click me"
              items={[
                { label: 'This is', onClick: () => {} },
                { label: 'The Menu', onClick: () => {} },
                { label: 'Component', onClick: () => {} },
              ]}
            />
          ) : (
            <Box fill align="start" justify="start" >
        <Select
          id="select"
          name="select"
          placeholder="Select"
          value={value}
          options={options}
          onChange={({ option }) => setValue(option)}
          open
        />
      </Box>
          )
        }
      </ResponsiveContext.Consumer>
    </Header>
        </Box>
    )
}

export default Home
