import React from "react";

import { useState, useEffect } from "react";

import { Link } from "react-router-dom";

import { getAll } from "../../services/NewsServicep/NewsServicep";

import {
  Box,
  Grommet,
  Header,
  Table,
  TableBody,
  TableCell,
  TableHeader,
  TableRow,
  Image,
  Text,
  Heading,
  Grid,
  
} from "grommet";
import { grommet } from "grommet/themes";

export const NewsList = () => {
  const [news, setNews] = useState();

  useEffect(() => {
    async function retrieveNews() {
      await getAll().then((result) => setNews(result));
      console.log(news);
    }
    retrieveNews();
  }, []);

  return (
    // const CollapsableNav = () => (
    <Grommet theme={grommet}>
      {/* <Box
        width="100vw"
        height="100vh"
        alignContent="center"
        pad="large"
        overflow="auto"
      >
        <Table>
          <TableHeader>
            <TableRow>
              <TableCell scope="col" border="bottom">
                <strong> Image </strong>
              </TableCell>
              <TableCell scope="col" border="bottom">
                <strong> News</strong>
              </TableCell>
            </TableRow>
          </TableHeader>
          <TableBody>
            {news &&
              news.map((newslist, i) => (
                <TableRow>
                  <TableCell>
                    <Image
                      src={`../../images/${newslist && newslist.Image}`}
                      width="20%"
                    />

                    
                  </TableCell>
                  <TableCell>
                    <Link to={`/FullNews/${newslist.id}`}>
                      {newslist.NewsTitle}
                    </Link>
                  </TableCell>
                </TableRow>
              ))}
          </TableBody>
        </Table>
      </Box> */}



<Box basis="full" pad="medium" width="full">
          <Heading size="small" textAlign="center">
            All News
          </Heading>
          <Grid gap="20px">
            {news &&
              news.map((n, index) => (
                <Card key={index} direction="row" height="10vh">
                  <Image src={`../images/${n && n.Image}`} width="30%"></Image>
                  <NavLink
                    to={`/News/${n.id}`}
                    onClick={() => updateCount(n.id, n.Count)}
                    style={{
                      textDecoration: "none",
                      marginTop: "3vh",
                      marginLeft: "1vw",
                    }}
                  >
                    <Text color="black" weight="bold">
                      {n.NewsTitle}
                    </Text>
                  </NavLink>
                </Card>
              ))}
          </Grid>
        </Box>
    </Grommet>
  );
};

export default NewsList;
