import React, { Component } from "react";

import { BrowserRouter as Router, Route, Switch } from "react-router-dom";

import { Box, Grommet } from "grommet";
import {
  UserAdmin as AdminIcon,
  Analytics as statisticIcon,
  UserAdd as requestIcon,
} from "grommet-icons/icons";

import { theme } from "../../theme";
import { Sidebar } from "./Sidebar";
import { NewsList } from "./NewsList";
import { MostReadNewsStat } from "./MostReadNewsStat";
import UserRequests from "./UserRequests";
import FullNews from "./FullNews";
import { useSelector } from "react-redux";
import { GuardProvider, GuardedRoute } from 'react-router-guards'
const items = [
  {
    label: "Most Read News",
    Icon: statisticIcon,
    path: "/MostReadNewsStat",
  },
  {
    label: "News Reporter Requests",
    Icon: requestIcon,
    path: "/UserRequests",
  },
];
var islogged;

export class IndexApp extends Component {

 islogged = useSelector((state) => state.islogged)
 requireLogin = (to, from, next) => {
    if (to.meta.auth) {
      if (islogged) {
        next()
      }
      next.redirect('/Login')
    } else {
      next()
    }
  }
  render() {
    return (
      <Router>
        <Grommet theme={theme} full>
          <Box direction="row" fill>
            <Sidebar
              appIcon={<AdminIcon></AdminIcon>}
              appName="Admin"
              items={items}
              // userSession={userSession}
            />
            <Box flex>
            <GuardProvider guards={[requireLogin]}>
              <Switch>
              <GuardedRoute path='/Admin/IndexApp' exact component={NewsList} meta={{ auth: true }}/>
              <GuardedRoute path='/Admin/IndexApp' exact component={NewsList} meta={{ auth: true }}/>
              <GuardedRoute path='/Admin/IndexApp' exact component={NewsList} meta={{ auth: true }}/>
              <GuardedRoute path='/Admin/IndexApp' exact component={NewsList} meta={{ auth: true }}/>
              <GuardedRoute path='/Admin/IndexApp' exact component={NewsList} meta={{ auth: true }}/>
                <Route path="/Admin/IndexApp" component={NewsList} />
                <Route path="/Admin" exact component={NewsList} />
                <Route path="/" exact component={NewsList} />
                <Route path="/MostReadNewsStat" component={MostReadNewsStat} />
                <Route path="/UserRequests" component={UserRequests} />
                <Route path="/FullNews/:id" component={FullNews} />
                <Route path="/FullNewsNoDelete" component={FullNews} />
              </Switch>
              </GuardProvider>
            </Box>
          </Box>
        </Grommet>
      </Router>
    );
  }
}
