import React, { useEffect, useState } from 'react'
import { Box, Heading,Image, Button, Select, TextInput, Text } from 'grommet'
import { useParams } from 'react-router-dom';
import NewsDataService from "./../services/NewsService/NewsService";
import IssueDataService from "./../services/IssueService/IssueService";
import ReactHtmlParser from 'react-html-parser';
function News() {
    const [news,setNews]=useState()
    const [message,setMessage]=useState('');
    const [value, setValue] = useState('');
    const [email, setEmail] = useState('');
    const [issue,setIssue]=useState(false);
    const {id}=useParams();
    useEffect(()=>{
        retrieveNews();
    },[])

    const retrieveNews=()=>{
        NewsDataService.FindOne(id).then(response=>{
            setNews(response.data);
            console.log(response.data);
           
        }).
        catch(err=>{
            if(err)
            {
                console.log(err);
            }
        })

    }

   
    const saveIssue=()=>{
        const data={
            email:email,
            issue:value,
            nId:news.id
        }
        console.log(data)
        IssueDataService.create(data).then(response=>{
            
            setIssue(false)
            setTimeout( setMessage('Issue Reported Successfully'),2000);
            setIssue(false)
            console.log(response.data)
        }).
        catch(err=>{
            if(err)
            {
                console.log(err);
            }
        })
      
    }

    console.log(news)
    return (
        <Box  height='full' align='center' width='full'>
          <Box background='light-2' width='large' align='center' height='full' overflow='auto'>
              <Heading size='small'> {news && news.NewsTitle}</Heading>
              <Image src={`../images/${news && news.Image}`} fill='horizontal' />
              <p>{news && news.user.Uname}</p>
              <p>{news && news.user.createdAt.slice(0,10)}</p>
               <Box>{news && ReactHtmlParser(news.News)}</Box>
               <Button primary label='Report' onClick={()=>setIssue(true)}></Button>
               {issue? 
               <Box size='small'>
                   <Heading>Report Issue</Heading>
                   <Select
                   placeholder="Choose Issue"
      options={['Child Abuse', 'Promotes terrorism', 'Hateful content']}
      value={value}
      size='small'
      onChange={({ option }) => setValue(option)}
    /><br></br>
    <TextInput type='text' name='email' required onChange={(event)=>setEmail(event.target.value)} placeholder='Email'/>
    <Button primary label='Report' onClick={saveIssue}></Button>
    {message && (
          <Box pad={{ horizontal: "medium" }}>
            <Text color="#00C781">{message}</Text>
          </Box>
        )}
                </Box>:<p></p>
               }
          </Box>
        </Box>
    )
}

export default News
