import React, { useState } from "react";
import NewsDataService from "./../services/NewsService/NewsService";
import { CKEditor } from "@ckeditor/ckeditor5-react";
import ClassicEditor from "@ckeditor/ckeditor5-build-classic";
import { Box, Button, FormField, TextInput, Heading } from "grommet";

function AddNews() {
  const initialNewsState = {
    id: null,
    NewsTitle: "",
    News: "",
    City: "",
    Image: "",
  };
  let user = sessionStorage.getItem("user");
  let u = JSON.parse(user);
  console.log(u.id);
  const [file, setFile] = useState("");
  const [filename, setFilename] = useState("");
  const [val, setVal] = useState("");
  const [news, setNews] = useState(initialNewsState);
  const [submitted, setSubmitted] = useState(false);

  const handleInputChange = (event) => {
    const { name, value } = event.target;
    setNews({ ...news, [name]: value });
  };

  const handleOnChange = (event, editor) => {
    const data = editor.getData();
    setVal(data);
  };

  const onChange = (event) => {
    setFile(event.target.files[0]);
    setFilename(event.target.files[0].name);
  };

  const saveNews = async () => {
    let formdata = new FormData();

    formdata.append("NewsTitle", news.NewsTitle);
    formdata.append("News", val);
    formdata.append("City", news.City);
    formdata.append("Image", filename);
    formdata.append("file", file);
    formdata.append("Rid", u.id);
    console.log(formdata);

    NewsDataService.create(formdata)
      .then((response) => {
        setNews({
          id: response.data.id,
          NewsTitle: response.data.NewsTitle,
          News: response.data.News,
          City: response.data.City,
          Image: response.data.Image,
        });

        setSubmitted(true);
        console.log(response.data);
      })
      .catch((e) => {
        console.log("Error");
        console.log(e);
      });
  };

  const addMoreNews = () => {
    setNews(initialNewsState);
    setSubmitted(false);
  };

  return (
    //     <Box>

    //     {submitted ? (
    //   <div>
    //   <h4>Hi !!! Posted SuccessFully</h4>
    //   <button className="btn btn-success" onClick={addMoreNews}>
    //   Post News
    //   </button>
    //   </div>
    //   ) : (

    //   <div>
    //   <h1>Post Today's Top News</h1>

    //     <div className="form-group t-2">
    //     <input
    //     type="text"
    //     className="form-control"
    //     id="title"
    //     required
    //     placeholder="title"
    //    value={news.NewsTitle}
    //    onChange={handleInputChange}
    //     name="NewsTitle"
    //   />
    // </div>

    // <div className="form-group t-2">
    // <CKEditor
    // name="News"
    // className="form-control"y

    // editor={ClassicEditor}
    // value={news.News}
    // onChange={handleOnChange}
    // />
    // </div>

    // <div className="form-group t-2">
    //     <input
    //     type="text"
    //     className="form-control"
    //     id="City"
    //     required
    //     placeholder="City"
    //     value={news.City}
    //    onChange={handleInputChange}
    //     name="City"
    //   />
    // </div>

    // <div className="form-group t-2">
    //     <input
    //     type="file"
    //     className="form-control"
    //     id="Image"
    //     required
    //    onChange={onChange}
    //     name="Image"
    //   />
    // </div>

    // <button className="btn btn-success" onClick={saveNews}>Post</button>

    // </div>
    // )}
    // </Box>

    <Box>
      {submitted ? (
        <Box>
          <Heading>Hi !!! Posted SuccessFully</Heading>
          <Button primary onClick={addMoreNews}>Post News</Button>
        </Box>
      ) : (
        <Box align='center'>
          <Heading>Post Today's Top News</Heading>

          <FormField
            name="NewsTitle"
            htmlfor="text-input-id"
            label="NewsTitle"
            required
          >
            <TextInput
              type="text"
              name="NewsTitle"
              value={news.NewsTitle}
              onChange={handleInputChange}
            />
          </FormField>

          <Box>
            {" "}
            <CKEditor
              name="News"
              className="form-control"
              y
              editor={ClassicEditor}
              value={news.News}
              onChange={handleOnChange}
            />
          </Box>

          <FormField name="City" htmlfor="text-input-id" label="City" required>
            <TextInput
              type="text"
              name="City"
              value={news.City}
              onChange={handleInputChange}
            />
          </FormField>
          <FormField
            name="Image"
            htmlfor="text-input-id"
            label="Image"
            required
          >
            <TextInput type="file" name="Image" onChange={onChange} />
          </FormField>

          <Button onClick={saveNews}>Post</Button>
        </Box>
      )}
    </Box>
  );
}

export default AddNews;
